import "./App.css";
import Axios from "axios";
import { useState } from "react";

function App() {
  const [artist, setArtist] = useState("");
  const [song, setSong] = useState("");
  const [lyrics, setLyrics] = useState("");
  const [error, setError] = useState("");

  async function searchLyrics() {
    if (artist === "" || song === "") {
      setError("Please enter both artist and song name.");
      return;
    }

    try {
      const response = await Axios.get(
        `https://api.lyrics.ovh/v1/${artist}/${song}`
      );
      if (response.data.lyrics) {
        setLyrics(response.data.lyrics);
        setError("");
      } else {
        setError("Lyrics not found for the given artist and song.");
      }
    } catch (error) {
      console.error("Error fetching lyrics:", error);
      setError(
        "An error occurred while fetching lyrics. Please try again later."
      );
    }
  }

  return (
    <div className="App">
      <h1>Lyrics Finder</h1>
      {error && <div className="error">{error}</div>}
      <input
        className="inp"
        type="text"
        placeholder="Artist name"
        value={artist}
        onChange={(e) => setArtist(e.target.value)}
      />
      <input
        className="inp"
        type="text"
        placeholder="Song name"
        value={song}
        onChange={(e) => setSong(e.target.value)}
      />
      <button className="btn" onClick={searchLyrics}>
        Search
      </button>
      <hr />
      <pre>{lyrics}</pre>
    </div>
  );
}

export default App;
